mapper.select:
SELECT ts.city, ts.site_id, ts.site_name, ts.`position`, ts.lng, ts.lat, ts.area_id
  FROM t_site ts
 WHERE 1 = 1

        AND ts.`position` =#{nodeAddr}


        AND ts.site_name =#{nodeName}

 LIMIT #{startIndex}, #{amount}

SELECT count(1)
  FROM t_site ts
 WHERE 1 = 1

        AND ts.`position` =#{nodeAddr}


        AND ts.site_name =#{nodeName}

mapper.update:
UPDATE t_site ts
   SET ts.site_name =#{rto.nodeName}, ts.city =#{rto.cityName}, ts.`position` =#{rto.nodeAddr},
       ts.lng =#{rto.lng}, ts.lat =#{rto.lat}, ts.area_id =#{rto.areaId}
 WHERE ts.site_id =#{rto.nodeId}
