mapper.select:
SELECT tv.tvis_json_id, tv.violation_id, tv.create_time, tv.vehs_idx, ts.site_name, tv.hphm, tv.wzlx, tvt.violation_type_name, sv.vehicle_type_name, tv.tvis_json_tbl, IF(tv.category = 0, '本市', '外埠') AS category, tv.create_time FROM t_violation tv
LEFT JOIN t_camera tc ON tc.camera_id = tv.camera_id LEFT JOIN t_site ts ON tc.site_id = ts.site_id LEFT JOIN t_area ta ON ta.area_id = ts.area_id LEFT JOIN t_special_vehicle_type sv ON sv.vehicle_type_num = tv.cllxzfl LEFT JOIN t_violation_type tvt ON tv.wzlx = tvt.violation_type_code WHERE 1 = 1
AND tv.hphm LIKE CONCAT('%', #{hphm}, '%')
AND ts.site_name LIKE CONCAT('%', #{addr}, '%')
AND tv.wzlx =#{illType}
AND tv.cllxzfl =#{vType}
AND category = 0
AND category = 1
AND tv.create_time >= #{startTime}
AND #{endTime} >= tv.create_time
ORDER BY tv.create_time DESC LIMIT #{startIndex}, #{amount}

SELECT count(tv.violation_id) FROM t_violation tv LEFT JOIN t_camera tc ON tc.camera_id = tv.camera_id LEFT JOIN t_site ts ON tc.site_id = ts.site_id LEFT JOIN t_area ta ON ta.area_id = ts.area_id LEFT JOIN t_special_vehicle_type sv ON sv.vehicle_type_num = tv.cllxzfl WHERE 1 = 1
AND tv.hphm like CONCAT(#{hphm}, '%')
AND ts.site_name =#{addr}
AND tv.wzlx =#{illType}
AND tv.cllxzfl =#{vType}
AND category = 0
AND category = 1
AND tv.create_time >= #{startTime}
AND #{endTime} >= tv.create_time

SELECT tsv.vehicle_type_num, tsv.vehicle_type_name
  FROM t_special_vehicle_type tsv

      WHERE `level` =#{level}

SELECT tv.tvis_json_id, tv.vehs_idx, tv.wzlx, tv.tvis_json_tbl, tv.category
  FROM t_violation tv

 WHERE tv.violation_id =#{tvId}

SELECT tv.tvis_json_id, tv.violation_id, tv.hphm, tv.create_time, tr.road_name, tv.wzlx, tv.tvis_json_tbl FROM t_violation tv
LEFT JOIN t_camera tc ON tv.camera_id = tc.camera_id LEFT JOIN t_site ts ON tc.site_id =ts.site_id LEFT JOIN t_road_seg trs ON trs.src_site_id = ts.site_id LEFT JOIN t_road tr ON tr.road_id = trs.road_id WHERE tv.hphm =#{hphm} AND road_name IS NOT NULL UNION ALL SELECT tv.tvis_json_id, tv.violation_id, tv.hphm, tv.create_time,tr.road_name, tv.wzlx, tv.tvis_json_tbl FROM t_violation tv
LEFT JOIN t_camera tc ON tv.camera_id = tc.camera_id LEFT JOIN t_site ts ON tc.site_id =ts.site_id LEFT JOIN t_road_seg trs ON trs.dst_site_id = ts.site_id LEFT JOIN t_road tr ON tr.road_id = trs.road_id WHERE tv.hphm =#{hphm} AND road_name IS NOT NULL LIMIT #{startIndex}, #{amount}

SELECT ta.area_name, COUNT(p.violation_id) AS `count` FROM t_area ta LEFT JOIN t_site ts ON ta.area_id = ts.area_id LEFT JOIN t_camera tc ON tc.site_id = ts.site_id LEFT JOIN ( SELECT tv.violation_id, tv.camera_id FROM t_violation tv WHERE 1 = 1
AND tv.create_time > #{startTime}
AND #{endTime} > tv.create_time
AND category = 0
AND category = 1
) p ON p.camera_id = tc.camera_id GROUP BY ta.area_id ORDER BY COUNT(p.violation_id) DESC LIMIT 10

SELECT COUNT(tv.violation_id) AS count FROM t_violation tv LEFT JOIN t_camera tc ON tc.camera_id = tv.camera_id LEFT JOIN t_site ts ON tc.site_id = ts.site_id LEFT JOIN t_area ta ON ta.area_id = ts.area_id LEFT JOIN t_special_vehicle_type sv ON sv.vehicle_type_num = tv.cllxzfl WHERE 1 = 1
AND tv.hphm LIKE CONCAT('%', #{hphm}, '%')
AND ts.site_name LIKE CONCAT('%', #{addr}, '%')
AND tv.wzlx =#{illType}
AND tv.cllxzfl =#{vType}
AND category = 0
AND category = 1
AND tv.create_time >= #{startTime}
AND #{endTime} >= tv.create_time

SELECT tv.tvis_json_id, tv.violation_id, tv.create_time, ts.site_name, tv.hphm, tv.wzlx, tvt.violation_type_name, sv.vehicle_type_name, tv.tvis_json_tbl, tv.create_time FROM t_violation tv LEFT JOIN t_camera tc ON tc.camera_id = tv.camera_id LEFT JOIN t_site ts ON tc.site_id = ts.site_id LEFT JOIN t_area ta ON ta.area_id = ts.area_id LEFT JOIN t_special_vehicle_type sv ON sv.vehicle_type_num = tv.cllxzfl LEFT JOIN t_violation_type tvt ON tv.wzlx = tvt.violation_type_code
WHERE 1 = 1
AND tv.hphm like CONCAT(#{hphm}, '%')
AND ts.site_name =#{addr}
AND tv.wzlx =#{illType}
AND tv.cllxzfl =#{vType}
AND category = 0
AND category = 1
AND tv.create_time >= #{startTime}
AND #{endTime} >= tv.create_time
LIMIT #{startIndex}, #{amount}

SELECT COUNT(tv.violation_id)
  FROM t_violation tv
 WHERE tv.hphm =#{hphm}
