mapper.select:
   SELECT zr.id, zr.order_no, zr.customer_id, zr.amount, zr.operator, zr.create_time, zc.customer_corporateName
     FROM zjc_recharge_record zr
LEFT JOIN zjc_customer zc
       ON zr.customer_id = zc.customer_id
    WHERE 1 = 1

            AND zr.customer_id = #{customerId}


            AND zr.order_no =#{orderno}

    LIMIT #{startIndex}, #{amount}

SELECT count(1)
  FROM zjc_recharge_record zr
 WHERE 1 = 1

         AND zr.customer_id = #{customerId}


         AND zr.order_no =#{orderno}
